,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'select',
'    metric,',
'    the_score,',
'    max_value ',
'from',
'    (',
'        select  ''Ticket is not closed'' metric, ',
'                decode(date_closed,null,0,20) the_score,',
