'    last_name               varchar2(255),',
'    country_id              varchar2(255),',
'    email                   varchar2(255),',
'    additional_info         varchar2(4000),',
'    flex_01                 varchar2(4000),',
'    flex_02                 varchar2(4000),',
'    flex_03                 varchar2(4000),',
'    flex_04                 varchar2(4000),',
'    --',
'    row_version             number,',
'    created_by              varchar2(255)       not null,',
'    created                 timestamp with time zone,',
'    updated_by              varchar2(255),',
'    updated                 timestamp with time zone )',
'/',
'',
'create unique index eba_intrack_users_uk on eba_intrack_users (username);',
'',
'create index eba_intrack_us_acc_lv_i on eba_intrack_users (access_level_id);',
'',
'create or replace trigger eba_intrack_users_biu',
'    before insert or update on eba_intrack_users',
